return $input.all().filter(item => {
  return item.json.organizer?.email === 'saajida.fhub@gmail.com'; // replace with your calendar email
});

return $input.all().map(item => {
  const ev = item.json;                       // shortcut
  const attendees = ev.attendees ?? [];

  // Basic counts
  const invited     = attendees.length;
  const rsvpYes     = attendees.filter(a => a.responseStatus === 'accepted').length;
  const rsvpNo      = attendees.filter(a => a.responseStatus === 'declined').length;
  const noResponse  = attendees.filter(a => a.responseStatus === 'needsAction').length;

  // Invitee e-mails as a flat list
  const inviteeEmails = attendees
      .map(a => a.email || a.emailAddress || '')
      .filter(Boolean);

  // Date / time formatting
  const startISO = ev.start?.dateTime || '';
  let startDate = '', startTime = '';

  if (startISO) {
    const d = new Date(startISO);
    startDate = d.toISOString().split('T')[0];          // yyyy-mm-dd
    startTime = d.toLocaleTimeString('en-US', {
      hour:   '2-digit',
      minute: '2-digit',
      hour12: false,
      timeZone: 'Asia/Dubai',                           // keep in sync with calendar TZ
    });
  }

  return {
    json: {
      /* renamed so it will not clash with Gmail id later */
      sessionId: ev.id,

      title:     ev.summary,
      startISO,
      startDate,
      startTime,

      invited,
      rsvpYes,
      rsvpNo,
      noResponse,
      inviteeEmails,

      meetLink:  ev.hangoutLink ?? '',
      status:    ev.status,
    }
  };
});

return $input.all().map(item => {
  const json = item.json;
  const summaryData = json.data?.summary || {};

  const invited = json.Invited || 0;
  const rsvpedYes = json.RSVPed_Yes || 0;
  const rsvpedNo = json.RSVPed_No || 0;

  const attended = parseInt(json["Ôªøtotal_attendees"] || 0);
  const emailsJoined = (json.participant_emails || "").split(",").map(e => e.trim());
  const invitedEmails = json.Invitees || [];

  const uninvited = emailsJoined.filter(email => !invitedEmails.includes(email));
  const uninvitedCount = uninvited.length;

  const avgDuration = json.average_duration || "00:00:00";
  const actionItems = summaryData.action_items || "";
  const keywords = summaryData.keywords || [];

  const actionCount = (actionItems.match(/\n/g) || []).length;

  return {
    json: {
      sessionTitle: json.summary,
      sessionId: json.session_id,
      startTime: json.start_time,
      endTime: json.end_time,
      invited,
      rsvpedYes,
      rsvpedNo,
      attended,
      uninvitedCount,
      lateJoiners: json.late_joiners,
      avgDuration,
      actionCount,
      keywords,
      firefliesSummary: summaryData.short_summary || "",
      discussion: summaryData.bullet_gist || ""
    }
  };
});

return $input.all().map(item => {
  let report = item.json.output || "";

  // Remove unnecessary headers
  report = report.replace(/\*\*Meeting Summary:\*\*\n*/, "");

  // Split sections (like Overview, Metrics, Keywords)
  const sections = report.split(/\n{2,}/g); // Split on 2+ line breaks
  let htmlParts = [];

  for (let section of sections) {
    // Section heading in bold
    const headingMatch = section.match(/^\*\*(.*?)\*\*/);
    if (headingMatch) {
      const heading = headingMatch[1];
      htmlParts.push(`<h3>${heading}</h3>`);
      section = section.replace(/^\*\*.*?\*\*\n*/, ""); // Remove heading line
    }

    // Convert bullet points and inline bold
    section = section
      .replace(/\*\*(.*?)\*\*/g, "<strong>$1</strong>") // Bold
      .replace(/‚Ä¢ (.*)/g, "<li>$1</li>")               // Bullets with ‚Ä¢
      .replace(/\n/g, "<br>");                         // Line breaks

    // Wrap <li> items in <ul> if any
    if (section.includes("<li>")) {
      section = `<ul>${section}</ul>`;
    }

    htmlParts.push(`<p>${section}</p>`);
  }

  const htmlContent = `<div style="font-family: Arial, sans-serif;">${htmlParts.join("")}</div>`;

  return {
    json: {
      subject: "Meeting Report - Test Event 1",
      plainText: report,
      html: htmlContent
    }
  };
});
